/**
 * CSS 模块类型声明
 * 为 @quizerjs/theme 包导出的 CSS 文件提供 TypeScript 类型支持
 */

declare module '@quizerjs/theme/solarized-light.css' {
  const content: string;
  export default content;
}

declare module '@quizerjs/theme/solarized-dark.css' {
  const content: string;
  export default content;
}

declare module '@quizerjs/theme/light.css' {
  const content: string;
  export default content;
}

declare module '@quizerjs/theme/dark.css' {
  const content: string;
  export default content;
}
